import React, { useEffect, useRef } from 'react';
import cytoscape from 'cytoscape';
import cola from 'cytoscape-cola';

cytoscape.use(cola);

const Network = ({ data }) => {
  const cyRef = useRef(null);

  useEffect(() => {
    if (!data) return;
    
    const cy = cytoscape({
      container: cyRef.current,
      elements: [],
      style: [
        {
          selector: 'node',
          style: {
            'background-color': 'data(visitedColor)',
            label: 'data(label)',
            color: '#fff',
            'text-valign': 'center',
            'text-halign': 'center',
          },
        },
        {
          selector: 'edge',
          style: {
            width: 2,
            'line-color': '#ccc',
            'target-arrow-shape': 'triangle',
            'target-arrow-color': '#ccc',
            'curve-style': 'bezier',
          },
        },
      ],
      layout: {
        name: 'preset',
      },
    });

    const nodeVisits = {}; // Track node visits and steps

    // Process each step in the walk
    data.walk.forEach((state, index) => {
      const x = state % data.grid_x;
      const y = Math.floor(state / data.grid_x);
      const id = `${x},${y}`;

      // Record visit and step
      if (!nodeVisits[id]) {
        nodeVisits[id] = { count: 0, steps: [] };
        // create node on the first visit
        cy.add({
          group: 'nodes',
          data: { id: id, visitedColor: '#5f5', label: `${id}` },
          position: { x: x * 50, y: y * 50 },
        });
      }
      nodeVisits[id].count += 1;
      nodeVisits[id].steps.push(index);

      // Connect nodes with directed edges, considering repeated edges
      if (index > 0) {
        const prevState = data.walk[index - 1];
        const prevX = prevState % data.grid_x;
        const prevY = Math.floor(prevState / data.grid_x);
        const sourceId = `${prevX},${prevY}`;
        cy.add({
          group: 'edges',
          data: {
            id: `e${index}`,
            source: sourceId,
            target: id,
          },
        });
      }
    });

    // Optionally, update node labels or styles based on visit count or steps here

    // Apply layout
    cy.layout({ name: 'preset' }).run();

  }, [data]);

  return <div ref={cyRef} style={{ width: '100%', height: '600px' }} />;
};

export default Network;
