import React, {useState, useEffect, useCallback} from 'react';
import axios from 'axios';
import Network from './components/Network';
import './index.css';
import MultipleSimulationsView from './components/MultipleSimulationsView';

function InputFields({ setValues }) {
  return (
      <>
          <p>Enter the size of the grid (x, y):</p>
          <input type="number" onChange={e => setValues('gridX', e.target.value)}/>
          <input type="number" onChange={e => setValues('gridY', e.target.value)}/>
          <p>Choose the number of the steps!</p>
          <input type="number" onChange={e => setValues('n', e.target.value)}/>
          <p>Enter the desired state (x, y):</p>
          <input type="number" onChange={e => setValues('targetX', e.target.value)}/>
          <input type="number" onChange={e => setValues('targetY', e.target.value)}/>
      </>
  );
}

function App() {
    const [values, setValues] = useState({
        n: '',
    targetX: '',
    targetY: '',
    gridX: '',
    gridY: '',
  });
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

    // eslint-disable-next-line react-hooks/exhaustive-deps
  const fetchData = useCallback(async () => {
    setIsLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/process', {
        ...values,
      });
      setData(response.data);
    } catch (error) {
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  });

  useEffect(() => {
      if (values.gridX && values.gridY && values.targetX && values.targetY) {
          fetchData();
      }
  }, [fetchData, values]);

  return (
    <div>
      <InputFields setValues={(key, value) => setValues(prev => ({ ...prev, [key]: value}))} />
      <button onClick={fetchData}>Send Data</button>
        {isLoading && <p>Loading data...</p>}
        {error && <p>Error: {error}</p>}
      <div id="canvas-container">
        <div id="canvas"></div>
      </div>
      <p id="hitting_time">{data && `Hitting time: ${data.hitting_time}`}</p>
      <p id="mixing_time">{data && `Mixing time: ${data.mixing_time}`}</p>
        {!isLoading && data && <Network data={data} />}
      <div>
          {values.gridX && values.gridY && values.targetX && values.targetY && (
              <MultipleSimulationsView {...values} />
          )}
      </div>
   </div>

  );
}

export default App;
