import React, { useEffect, useRef } from 'react';
import cytoscape from 'cytoscape';
import cola from 'cytoscape-cola';

cytoscape.use(cola);

const Network = ({ data }) => {
  const cyRef = useRef(null);

  useEffect(() => {
    if (!data) return;

    const cy = cytoscape({
      container: cyRef.current,
      elements: [],
      style: [
        {
          selector: 'node',
          style: {
            'background-color': 'data(visitedColor)',
            label: 'data(label)',
            color: '#fff',
            'text-valign': 'center',
            'text-halign': 'center',
          },
        },
        {
          selector: 'edge',
          style: {
            width: 2,
            'line-color': '#ccc',
            'target-arrow-shape': 'triangle',
            'target-arrow-color': '#ccc',
            'curve-style': 'bezier',
          },
        },
      ],
      layout: {
        name: 'preset',
      },
    });

    // Generate grid
    const visitedNodes = new Set();
    data.walk.forEach((step, index) => {
      visitedNodes.add(`${step.x},${step.y}`);
      // Add nodes for the walk with a special color and label
      cy.add({
        group: 'nodes',
        data: { id: `${step.x},${step.y}`, visitedColor: '#f00', label: `${index}` },
        position: { x: step.x * 50, y: step.y * 50 },
      });
      // Connect nodes with directed edges
      if (index > 0) {
        const source = data.walk[index - 1];
        cy.add({
          group: 'edges',
          data: {
            id: `e${index}`,
            source: `${source.x},${source.y}`,
            target: `${step.x},${step.y}`,
          },
        });
      }
    });

    // Fill in the rest of the grid
    for (let x = 0; x < data.width; x++) {
      for (let y = 0; y < data.height; y++) {
        const id = `${x},${y}`;
        if (!visitedNodes.has(id)) {
          cy.add({
            group: 'nodes',
            data: { id: id, visitedColor: '#666', label: '' },
            position: { x: x * 50, y: y * 50 },
          });
        }
      }
    }

    // Apply layout
    cy.layout({ name: 'preset' }).run();

  }, [data]);

  return <div ref={cyRef} style={{ width: '100%', height: '600px' }} />;
};

export default Network;
